import React from 'react';
import {navigate} from '@reach/router';
import axios from 'axios';

export default props => {

    const{setInfo, info} = props

    const rmFromDom = prodID => {
        setInfo(info.filter(prod => prod._id !== prodID))
    }

    const deleteProd = (prodID) =>{
        axios.delete(`http://localhost:8000/api/products/${prodID}`)
            .then(res=>{
                rmFromDom(prodID)
            })
    }

    return(
        <div>
                <h2>Product List: </h2>
            <div className="looks">

                    {
                        info.map((item, i) =>
                            <div key={i}>
                                <p  onClick={(e)=> {navigate(`products/${item._id}`)}}> {item.title}</p> 
                                <button onClick={deleteProd} >Delete</button>
                            </div>
                        )
                    }

            </div>
        </div>
    )


}
